function restrictToNumberHyphen(r){$(r).val($(r).val().replace(/[^\d-]/g,""))}function restrictToNum(r){var a=$(r).val().replace(/\D/g,"");a.startsWith("0")&&(a=a.replace(/^0+/,"")),$(r).val(a)}function restrictToUniformNum(r){var a=$(r).val().replace(/\D/g,"");8<a.length&&(a=a.slice(0,8)),$(r).val(a)}function restrictToValidChars(r){$(r).val($(r).val().replace(/[^a-zA-Z0-9._@-]/g,""))}function formatNumber(r){return r.toString().replace(/\B(?=(\d{3})+(?!\d))/g,",")}function unformatNumber(r){return r.replace(/,/g,"")}function unformatOnFocus(r){var a=$(r).val();$(r).val(unformatNumber(a))}function formatOnBlur(r){var a=$(r).val();""!==a&&$(r).val(formatNumber(a))}